<script>
export default {
    name: 'CheckOutComponent',
    data(){
        return{
            displayCart: true,
            displayDeliveryInfo: true,
            displayPaymentInfo: false,
            isLoading: false,
            authUser: window.authUser,
            checkOutName: window.authUser.name,
            checkOutAddress: window.authUser.address,
            checkOutEmail: window.authUser.email,
            checkOutCity: window.authUser.city,
            checkOutState: window.authUser.state,
            checkOutPhone: window.authUser.phone,
            csrf: document.querySelector('meta[name="csrf-token"]').getAttribute('content')   
        }
    },
    computed: {
        getAllCart(){ 
            return this.cart = this.$store.getters.getCart
        },
        cartNo(){
            return this.$store.getters.addCart
        },
        subTotal(){
            return this.$store.getters.subTotal
        },
        deliveryFee(){
            return this.$store.getters.deliveryFee    
        },
        estimatedTotal(){
            return this.subTotal + this.deliveryFee
        },
        serviceFee(){
            return Math.ceil(parseFloat((1.4 / 100) * this.estimatedTotal).toFixed(2)) 
        },
        totalFee(){
            return this.estimatedTotal + this.serviceFee
        }
    },
    methods:{
        async minusProduct(id, quantity){
            if(quantity > 1){
                this.isLoading = true
                quantity--
                axios.post(`/reducebyone/${id}/${quantity}`).then(response => {
                    let cart = this.cart = response.data
                    this.$store.dispatch("allCartFromDatabase")
                    this.isLoading = false
                }).catch(error => {
                    console.log(error)
                })
            } 
        },
        async addProduct(id, quantity){
            if(quantity >= 1){
                this.isLoading = true
                quantity++
                axios.post(`/addbyone/${id}/${quantity}`).then(response => {
                    this.cart = response.data
                    this.$store.dispatch("allCartFromDatabase")
                    this.isLoading = false
                }).catch(error => {
                    console.log(error)
                })
            }
        },
        async removeProduct(id){
            this.isLoading = true
            axios.post(`/removeproduct/${id}/`).then(response => {
                this.cart = response.data
                this.$store.dispatch("allCartFromDatabase")
                this.isLoading = false
            }).catch(error => {
                console.log(error)
            })
        },
        confirmOrder(){
            this.displayDeliveryInfo = !this.displayDeliveryInfo
            this.displayPaymentInfo = !this.displayPaymentInfo
        },
        changeDeliveryInfo(){
            this.displayPaymentInfo = !this.displayPaymentInfo    
            this.displayDeliveryInfo = !this.displayDeliveryInfo
        },
        async confirmPayment(){
            this.isLoading = true
            // alert()
            axios.post('/flutterwave/submit', {
                // name: this.signUpName,
                // email: this.signUpEmail,
                // password: this.signUpPassword
            }).then(response => {
                
                this.isLoading = false
                console.log(response.data)

                // setTimeout(()=>{
                //     window.location = '/'
                // }, 3000)

            }).catch(error => {
                console.log(error)
            })
        },
    }
       
}
</script>