</template>

<script>
import SearchBar from './SearchBarComponent'
import CartComponent from './CartComponent'
import LoginComponent from './LoginComponent'
import UserAccountSettingsComponent from './UserAccountSettingComponent'
import ProductComponent from './productComponent'
import UserSubscription from './UserSubscriptionComponent'
import UserOrder from './UserOrdersComponent'
import UserTrackOrder from './UserTrackOrderComponent'
import UserProfile from './UserProfileComponent'
import UserResetPassword from './UserResetPasswordComponent'

export default {
    name: 'Nav',
    mounted() {
        this.$store.dispatch('allCartFromDatabase')
        this.$store.dispatch('loginUserFromDatabase')
    },
    components:{
        SearchBar,
        CartComponent,
        LoginComponent,
        UserAccountSettingsComponent,
        ProductComponent,
        UserSubscription,
        UserOrder,
        UserTrackOrder,
        UserProfile,
        UserResetPassword
    },
    props: ['account'],
    data(){
        return{
            displayCart: false,
            displayLogin: false,
            displayProduct: false,
            displayAccountSettings: false,
            displayCategoryMobile: false,
            displayDrawerContent: false,
            displayDrawerCategory: true,
            displayDrawerAccount: false,
            displayUserSubscription: false,
            displayUserOrder: false,
            displayTrackOrder: false,
            displayUserProfile: false,
            displayUserResetPassword: false,
            displayBottomNav: true,
            displayNavIcons: true,
            displaySearchBox: false,
            showCallBack: false,
            isLoading: false,
            categories: [],
            search_box: '',
            products: [],
        }
    },
    async created(){
        axios.get(`/allcategories`).then(response => {
            this.categories = response.data;
        }).catch(error => {
            console.log(error)
        })
    },
    methods:{
        searchIcon(){
            this.displayNavIcons = !this.displayNavIcons
            this.displaySearchBox = !this.displaySearchBox
            this.displayBottomNav = !this.displayBottomNav
        },
        closeSearchBox(){
            this.displayNavIcons = !this.displayNavIcons
            this.displaySearchBox = !this.displaySearchBox
            this.showCallBack = false
            this.displayBottomNav = true
            this.search_box = ''
        },
        async searchProduct(){
            let product = this.search_box
            if(product.length > 2){
                this.showCallBack = true
                this.isLoading = true
                axios.get(`/autosearch/product/${product}`, {
                    slug: this.product
                }).then(response => {
                    this.isLoading = false

                    // if(response.data.length == 0){
                    //     this.showCallBack = false
                    // }

                    this.products = response.data
                    console.log(response.data)
                
                }).catch(error => {
                    console.log(error)
                })
            }else{
                this.showCallBack = false
            }
        },
        drawerCategory(){
            this.displayDrawerCategory = !this.displayDrawerCategory 
            this.displayDrawerAccount = !this.displayDrawerAccount 
        },
        drawerAccount(){
            this.displayDrawerAccount = !this.displayDrawerAccount 
            this.displayDrawerCategory = !this.displayDrawerCategory 
        },
        drawerToggle(){
            this.displayDrawerContent = !this.displayDrawerContent 
            this.displayBottomNav = !this.displayBottomNav 
        },
        cartToggle(){ 
            this.displayCart = !this.displayCart 
            this.displayBottomNav = !this.displayBottomNav
            // this.displayDrawerContent = !this.displayDrawerContent
            this.cart = this.$store.dispatch("allCartFromDatabase")
        },
        loginToggle(){
            if(this.getUser != ''){
                this.displayAccountSettings = !this.displayAccountSettings
                this.displayBottomNav = !this.displayBottomNav
                this.displayDrawerContent = !this.displayDrawerContent
            }else{
                this.displayBottomNav = !this.displayBottomNav
                this.displayDrawerContent = !this.displayDrawerContent
                this.displayLogin = !this.displayLogin
            }
        },
        categoryBottomToggle(){
            this.displayCategoryMobile = !this.displayCategoryMobile
            this.displayBottomNav = !this.displayBottomNav
            this.displayDrawerContent = !this.displayDrawerContent
        },
        closeCategory(){
            this.displayCategoryMobile = !this.displayCategoryMobile
            this.displayBottomNav = !this.displayBottomNav
            this.displayDrawerContent = !this.displayDrawerContent
        },
        openSubscription(){
            this.displayUserOrder = false 
            this.displayTrackOrder = false 
            this.displayUserProfile = false 
            this.displayUserResetPassword = false 
            this.displayUserSubscription = !this.displayUserSubscription 
        },
        openMyOrders(){
            this.displayUserSubscription = false
            this.displayTrackOrder = false
            this.displayUserProfile = false
            this.displayUserResetPassword = false
            this.displayUserOrder = !this.displayUserOrder
        },
        openTrackOrder(){
            this.displayUserSubscription = false
            this.displayUserOrder = false
            this.displayUserProfile = false
            this.displayUserResetPassword = false
            this.displayTrackOrder = !this.displayTrackOrder
        },
        openEditProfile(){
            this.displayUserSubscription = false
            this.displayUserOrder = false
            this.displayTrackOrder = false
            this.displayUserResetPassword = false
            this.displayUserProfile = !this.displayUserProfile
        },
        openResetPassword(){
            this.displayUserSubscription = false
            this.displayUserOrder = false
            this.displayTrackOrder = false
            this.displayUserProfile = false
            this.displayUserResetPassword = !this.displayUserResetPassword
        }
    },
    computed: {
        getAllCart(){ 
            return this.$store.getters.getCart
        },
        getUser(){
            return this.$store.getters.getUser
        },
        cartNo(){
            return this.$store.getters.addCart
        },
        subTotal(){
            return this.$store.getters.subTotal
        },
        deliveryFee(){
            return this.$store.getters.deliveryFee    
        },
        estimatedTotal(){
            let productTotal = (this.subTotal * 0.143) + this.subTotal
            let deliveryTotal = (this.deliveryFee * 0.075) + this.deliveryFee
            return (parseFloat(productTotal) + parseFloat(deliveryTotal)).toLocaleString()
        }
    }
}
</script>