<script>
export default {
    name: 'TransactionSuccessfulComponent',
    data(){
        return{
            displayCart: true,
            displayPaymentSuccessful: true,
            displayTransactionSummary: false,
            displayTrackOrder: false,
            isLoading: false,
            authUser: window.authUser,
        }
    },
    props:[
        'subtotal', 'deliveryfee', 'servicefee', 'transactionid', 'orderid', 'bags'
    ],
    methods:{
        trackOrder(){
            this.displayTrackOrder = !this.displayTrackOrder
            this.displayTransactionSummary = !this.displayTransactionSummary
        },
        printReceipt(){
            window.location = `/order/print/${this.orderid}`
        }
    },
    mounted(){
        setTimeout(()=>{
            this.displayPaymentSuccessful = !this.displayPaymentSuccessful
            this.displayTransactionSummary = !this.displayTransactionSummary
        }, 2000)
    }
       
}
</script>